{% set doc1 = frappe.get_doc('Job Applicant', doc.job_applicant) %}
{% set current = doc.interview_round %}
{% set found = False %}
{% set nextRound = False %}

{% for i in doc1.custom_interview_rounds %}
  {% if not found and i.interview_rounds == current %}
    {% set found = true %}
    {% set nextRound = i.interview_rounds %}
  {% endif %}
{% endfor %}

{% if doc.custom_interview_type=="Offline" %}
    <p>Dear {{ doc1.applicant_name }},</p>

    <p>Greetings of the day!</p>

    <p>We are pleased to inform you that your interview has been scheduled for {{ doc.designation }} on {{ doc.scheduled_on }} at {{ doc.from_time }}.</p>
    <p>It will be a {{ doc.interview_round }}.</p>

    <p>Wishing you the best for your interview.</p>

    <p>Thanks and regards,</p>
    <p>HR-Team KoreCent</p>

{% else %}
    <p>Dear {{ doc1.applicant_name }},</p>

    <p>Greetings of the day!</p>

    <p>We are pleased to inform you that your interview has been scheduled for {{ doc.designation }} on {{ doc.scheduled_on }} at {{ doc.from_time }}.</p>
    <p>It will be a {{ doc.interview_round }}.</p>

    <p>Interview Link: {{doc.custom_interview_link}}</p>

    <p>Wishing you the best for your interview.</p>

    <p>Thanks and regards,</p>
    <p>HR-Team KoreCent</p>
{% endif %}
